import { Component } from '@angular/core';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.scss']
})
export class NavComponent {

  isOpenMenu:boolean = false;

  openMenu(){
    this.isOpenMenu = !this.isOpenMenu;
  }
}
